const int N = 2e5 + 10;

struct Tree {
    vvi g;
    vi parent;
    int timer = 1;
    vi tin, tout;
    vi dip;
    Tree(int n) {
        g.resize(n + 1);
        parent.resize(n + 1, -1);
        tin.resize(n + 1);
        tout.resize(n + 1);
        dip.resize(n + 1, 0);
    }

    void add_edge(int v, int u) {
        g[u].push_back(v);
        g[v].push_back(u);
    }

    void dfs(int v, int par = -1) {
        tin[v] = timer++;
        if (par == -1)
            par = v;
        parent[v] = par;
        for (int to : g[v]) {
            if (to == par)
                continue;
            dip[to] = dip[v] + 1;
            dfs(to, v);
        }
        tout[v] = timer++;
    }

    bool is_par(int u, int v) {
        return tin[u] <= tin[v] && tin[v] <= tout[u];
    }
};

