    da[x] = da[fx] + 1;
    if (da[x] >= db[x]) return;
    if (~ans) ans = max((db[x] - 1) << 1, ans);
    for (int y : a[x]) {
        if (y == fx) continue;
        if (db[x] + db[y] - 2 * db[LCA(x, y)] > 2) ans = -1;
        dfsa(y, x);
    }
}
