const int N = 2e5 + 5;
const int INF = 1e9;

int n, X, Y;
vector <int> red_nb[N], blue_nb[N];

int low[N], high[N], timer;
int depth[N], par[N];
void DFS_blue(int v)
{
    low[v] = high[v] = ++timer;
    for(int u: blue_nb[v])
    {
        if(u != par[v])
        {
            depth[u] = depth[v] + 1;
            par[u] = v;
            DFS_blue(u);
            high[v] = max(high[v], high[u]);
        }
    }
}

bool in_subtree(int u, int v)
{
    if(u == 0)
        return false;
    return (low[u] <= low[v] && high[u] >= low[v]);
}

bool far_blue(int u, int v)
{
    if(depth[u] > depth[v])
        swap(u, v);
    if(u == v || u == par[v] || u == par[par[v]])
        return false;
    if(depth[v] <= depth[u] + 2)
        if(in_subtree(u, v))
            return false;
    if(depth[v] <= depth[u])
        if(in_subtree(par[u], v))
            return false;

    return true;
}

bool special[N];
int ans = 2, red_dist[N];
void DFS_red(int v, int parent = -1)
{
    if(red_dist[v] > depth[v])
        return;
    ans = max(ans, depth[v] * 2);

    if(red_dist[v] == depth[v])
        return;
    if(special[v])
        ans = INF;

    for(int u: red_nb[v])
    {
            if(u != parent)
            {
                red_dist[u] = red_dist[v] + 1;
                DFS_red(u, v);
            }
    }

}

