        blue_nb[u].pb(v);
        blue_nb[v].pb(u);
    }

    DFS_blue(Y);

    for(int i = 1; i <= n; i++)
        for(int u: red_nb[i])
            if(far_blue(u, i))
                special[u] = special[i] = true;

    DFS_red(X);


    if(ans == INF)
        cout << -1 << endl;
    else
        cout << ans << endl;

