		read(x), read(y);
		a[x].push_back(y);
		a[y].push_back(x);
	}
	depth[0] = -1;
	work(y, 0);
	for (int i = 1; i < n; i++)
		if (dist(u[i], v[i]) >= 3) mark[u[i]] = mark[v[i]] = true;
		else {
			b[u[i]].push_back(v[i]);
			b[v[i]].push_back(u[i]);
		}
	work(x, 0, 0);
	int ans = 0;
	for (int i = 1; i <= n; i++)
		if (visited[i]) if (mark[i]) {
			printf("-1\n");
			return 0;
		} else ans = max(ans, depth[i] * 2);
	cout << ans << endl;
	return 0;
}

