	{
		int s=0;
		if (dep[x]>dep[y])  swap(x,y);
		for (int i=19; i>=0; i--)
			if (dep[f[y][i]]>=dep[x])
				y=f[y][i],s+=1<<i;
		if (x==y)  return s;
		for (int i=19; i>=0; i--)
			if (f[x][i]!=f[y][i])
				x=f[x][i],y=f[y][i],s+=2<<i;
		return s+2;
	}

