int n, x, y, first[maxn], dep[maxn], s[maxn][2], f[maxn][27], g[maxn], dis[maxn], cnt;
bool vis[maxn];
struct Edge {
	int to, next, ser;
} e[maxn<<2];

void Dfs(int u, int fa) {
	dep[u] = dep[fa] + 1;
	f[u][0] = fa;
	g[u] = dep[u];
	for(int i = first[u]; i; i = e[i].next) {
		int v = e[i].to;
		if(v == fa || e[i].ser) continue;
		Dfs(v, u);
		g[u] = max(g[v], g[u]);
	}
}

void add(int u, int v, int ser) {
	e[++cnt] = (Edge) {v, first[u], ser};
	first[u] = cnt;
}

int Lca(int u, int v) {
	if(dep[u] < dep[v]) swap(u, v);
	int dif = dep[u] - dep[v];
	per(i, 25, 0)
	    if(dif >= (1<<i)) {
	    	dif -= 1<<i;
	    	u = f[u][i];
	    }
	per(i, 25, 0)
	    if(f[u][i] != f[v][i]) {
	    	u = f[u][i];
	    	v = f[v][i];
	    }
	return f[u][0];
}


bool Bfs(int s) {
	memset(dis, 0x7f, sizeof(dis));
	queue <int> q;
	dis[s] = 0;
	q.push(s);
	while(!q.empty()) {
		int u = q.front();
		q.pop();
		for(int i = first[u]; i; i = e[i].next) {
			int v = e[i].to;
			if(!e[i].ser || dis[u] + 1 >= dep[v] || dis[u] + 1 >= dis[v]) continue;
			dis[v] = dis[u] + 1;
			q.push(v);
		}
	}
	rep(i, 1, n)
	    if(dis[i] < dep[i] && vis[i]) return 1;
	return 0;
}

