	struct edge{int to,nxt;}e[N<<1];
	void adde(int x,int y){
		e[++cnt].to=y; e[cnt].nxt=head[x]; head[x]=cnt;
	}
	void dfs(int u,int par){
		in[u]=++clk; q[clk]=u;
		for (int i=head[u],v;i;i=e[i].nxt)
			if (v=e[i].to,v!=par)
				dep[v]=dep[u]+1,dfs(v,u),q[++clk]=u;
		out[u]=clk;
	}
	inline bool isanc(int x,int y){return in[x]<=in[y]&&out[y]<=out[x];}
	inline int Min(int x,int y){return dep[x]<dep[y]?x:y;}
	void st_init(){
		rep (i,1,clk) st[i][0]=q[i],lg[i]=i==1?0:lg[i>>1]+1;
		rep (j,1,20)
			for (int i=1;i+(1<<j-1)<=clk;i++)
				st[i][j]=Min(st[i][j-1],st[i+(1<<j-1)][j-1]);
	}
	void init(int _n){
		n=_n; cnt=clk=0; memset(head,0,n+1<<2);
		rep (i,1,n-1){
			int x,y; scanf("%d%d",&x,&y);
			adde(x,y); adde(y,x);
		}
		dfs(1,0),st_init();
	}
	inline int lca(int x,int y){
		if (in[x]>in[y]) swap(x,y);
		if (isanc(x,y)) return x;
		x=out[x],y=in[y]; int t=lg[y-x+1];
		return Min(st[x][t],st[y-(1<<t)+1][t]);
	}
	inline int dis(int x,int y){return dep[x]+dep[y]-2*dep[lca(x,y)];}
}T1,T2;

