  void add(int from,int to) {
    con[++tot] = (edge) {fir[from],to};
    fir[from] = tot;
  }
  int dep[N],anc[N][MP];
  void dfs(int pos,int fa) {
    dep[pos] = dep[fa] + 1;
    anc[pos][0] = fa;
    for (int i = 1 ; i < MP ; ++ i) {
      anc[pos][i] = anc[anc[pos][i-1]][i-1];
      if (!anc[pos][i]) break;
    }
    for (int i = fir[pos] ; i ; i = con[i].la) {
      if (con[i].b == fa) continue;
      dfs(con[i].b,pos);
    }
  }
  int lca(int x,int y) {
    if (dep[x] < dep[y]) swap(x,y);
    for (int i = MP - 1 ; i >= 0 ; -- i)
      if (dep[anc[x][i]] >= dep[y])
	x = anc[x][i];
    if (x == y) return x;
    for (int i = MP - 1 ; i >= 0 ; -- i)
      if (anc[x][i] != anc[y][i])
	x = anc[x][i], y = anc[y][i];
    return anc[x][0];
  }
  int askdis(int x,int y) {
    int z = lca(x,y);
    return dep[x] + dep[y] - 2 * dep[z];
  }
  void fsd(int pos,int fa,int dep);
} rd,blu;
int ans;
void tree::fsd(int pos,int fa,int dep) {
  if (ans != -1) ans = max(ans,blu.askdis(pos,sty) * 2);
