    return;
  for (int i = fir[pos] ; i ; i = con[i].la) {
    if (con[i].b == fa) continue;
    if (blu.askdis(pos,con[i].b) >= 3) {
      ans = -1;
      return;
    }
    fsd(con[i].b,pos,dep+1);
  }
}
