        for (int p=T1.now[i];p;p=pre[p])
            {
            int child=son[p];
            if (T2.Get_Dis(child,i)>2)
                {
                puts("-1");
                exit(0);
                }
            }
        }
    printf("%d\n",ans*2);
}

void prepare()
{
    can[x]=1;
    st.push(x);
    while(!st.empty())
    {
        int x=st.front();st.pop();
        for (int p=T1.now[x];p;p=pre[p])
        {
            int child=son[p];
            if (can[child]) continue;
            if (T1.dep[child]<T2.dep[child]) can[child]=1,st.push(child);
        }
    }
}

int main()
{
