	}

	inline bool check_two(int x, int y) {return p[x] == y || p[y] == x || p[p[x]] == y || p[p[y]] == x || p[x] == p[y];}

	void init(int V_, int root_) {
		int i, u, v; V = V_, E = 0, root = root_;
		to = AL(2 * (V - 1)), first = AL(V), next = AL(2 * (V - 1));
