		p = AL(V), dep = AL(V), p[root] = dep[root] = 0, dfs(root);
		for (i = 1; i <= V; ++i)
			for (to[ u = first[i] ] == p[i] && (u = first[i] = next[u]); u; u = next[u])
				if (to[ next[u] ] == p[i]) next[u] = next[ next[u] ];
