	inline int Min(int x,int y){return dep[x]<dep[y]?x:y;}
	void st_init(){
		rep (i,1,clk) st[i][0]=q[i],lg[i]=i==1?0:lg[i>>1]+1;
		rep (j,1,18)
			for (int i=1;i+(1<<j-1)<=clk;i++)
				st[i][j]=Min(st[i][j-1],st[i+(1<<j-1)][j-1]);
	}
