int N, P, Q;
int A[1 << 18], B[1 << 18];
int C[1 << 18], D[1 << 18];
vector<int> X[1 << 18];
vector<int> Y[1 << 18];

// LCA
int par[1 << 18][22];

// 
int dist1[1 << 18];
int dist2[1 << 18];
bool rakuen[1 << 18];
bool used[1 << 18];

void dfs1(int pos, int dep) {
	dist1[pos] = dep;
	for (int i : X[pos]) {
		if (dist1[i] != -1) continue;
		dfs1(i, dep + 1);
	}
}

void dfs2(int pos, int dep) {
	dist2[pos] = dep;
	for (int i : Y[pos]) {
		if (dist2[i] != -1) continue;
		dfs2(i, dep + 1);
		par[i][0] = pos;
	}
}

void dfs3(int pos, int pre) {
	used[pos] = true;
	for (int i : X[pos]) {
		if (i == pre) continue;
		if (dist1[i] >= dist2[i]) continue;
		dfs3(i, pos);
	}
}

int prevs(int u, int x) {
	for (int i = 21; i >= 0; i--) {
		if (x >= (1 << i)) { x -= (1 << i); u = par[u][i]; }
	}
	return u;
}

int lca(int u, int v) {
	if (dist2[u] > dist2[v]) swap(u, v);
	v = prevs(v, dist2[v] - dist2[u]);
	if (u == v) return u;

	for (int i = 21; i >= 0; i--) {
		if (par[u][i] != par[v][i]) { u = par[u][i]; v = par[v][i]; }
	}
	return par[u][0];
}

int getdst(int u, int v) {
	int w = lca(u, v);
	return dist2[u] + dist2[v] - 2 * dist2[w];
}

int main() {
	cin >> N >> P >> Q;
	for (int i = 1; i <= N - 1; i++) {
		cin >> A[i] >> B[i];
		X[A[i]].push_back(B[i]);
		X[B[i]].push_back(A[i]);
	}
	for (int i = 1; i <= N - 1; i++) {
		cin >> C[i] >> D[i];
		Y[C[i]].push_back(D[i]);
		Y[D[i]].push_back(C[i]);
	}
	for (int i = 1; i <= N; i++) dist1[i] = -1;
	for (int i = 1; i <= N; i++) dist2[i] = -1;
	dfs1(P, 0);
	dfs2(Q, 0);
	dfs3(P, -1);

	for (int i = 1; i <= 21; i++) {
		for (int j = 1; j <= N; j++) par[j][i] = par[par[j][i - 1]][i - 1];
	}
	for (int i = 1; i <= N - 1; i++) {
		if (getdst(A[i], B[i]) <= 2) continue;
		rakuen[A[i]] = true;
		rakuen[B[i]] = true;
	}

	int Answer = 0;
	for (int i = 1; i <= N; i++) {
		if (used[i] == true && rakuen[i] == true) {
			cout << "-1" << endl;
			return 0;
		}
		if (used[i] == true) Answer = max(Answer, dist2[i] * 2);
	}
	cout << Answer << endl;
	return 0;
}

