using namespace std;
const int N=2e5+10;
int n,x,y;
vector<int> e1[N],e2[N];
int fa[N],dep[N],ance[18][N];
void dfs(int x){
    for (int i=0;i<e2[x].size();i++){
        int v=e2[x][i];
        if (fa[v]) continue;
        fa[v]=x;
        dep[v]=dep[x]+1;
        dfs(v);
    }
}
void init(){
    fa[1]=1;dfs(1);
    for (int i=1;i<=n;i++) ance[0][i]=fa[i];
    for (int i=1;i<18;i++)
    for (int j=1;j<=n;j++)
        ance[i][j]=ance[i-1][ance[i-1][j]];
}
int go(int x,int k){
    for (int i=0;i<18;i++)
    if (k>>i&1) x=ance[i][x];
    return x;
}
int lca(int x,int y){
    if (dep[x]<dep[y]) swap(x,y);
    x=go(x,dep[x]-dep[y]);
    for (int i=17;i>=0;i--)
    if (ance[i][x]!=ance[i][y]) x=ance[i][x],y=ance[i][y];
    return x!=y?fa[x]:x;
}
int dist(int x,int y){
    return dep[x]+dep[y]-2*dep[lca(x,y)];
}
int move(int x,int y){
    int LCA=lca(x,y);
    return y!=LCA?fa[y]:go(x,dep[x]-dep[y]-1);
}
void dfs(int x,int y,int fa){
    if (x==y) return;
    for (int i=0;i<e1[x].size();i++){
        int v=e1[x][i];
        if (v==fa) continue;
        if (dist(x,v)>2) puts("-1"),exit(0);
        if (v!=y) dfs(v,move(v,y),x);
    }
}
int ans;
void calc(int x,int y,int fa,int times){
    ans=max(ans,times+dist(x,y));
    if (x==y) return;
    for (int i=0;i<e1[x].size();i++){
        int v=e1[x][i];
        if (v==fa||v==y) continue;
        calc(v,move(v,y),x,times+1);
    }
}
int main()
{
    scanf("%d%d%d",&n,&x,&y);
    for (int i=1;i<n;i++){
        int a,b;
        scanf("%d%d",&a,&b);
        e1[a].push_back(b);
        e1[b].push_back(a);
    }
    for (int i=1;i<n;i++){
        int a,b;
        scanf("%d%d",&a,&b);
        e2[a].push_back(b);
        e2[b].push_back(a);
    }
    init();
    dfs(x,y,0);
    calc(x,y,0,0);
    printf("%d\n",ans*2);
    return 0;
}

#include <cstdio>
#include <vector>
using namespace std;

const int N = 200500;

vector<int> E[N];
vector<int> F[N];

const int LGN = 18;
int up[LGN][N];
int D[N];

void DFS(int x, int p = -1) {
    D[x] = (p == -1) ? 0 : D[p] + 1;
    up[0][x] = (p == -1) ? x : p;
    for (int d = 1; d < LGN; d++)
        up[d][x] = up[d - 1][up[d - 1][x]];
    for (int y : E[x]) {
        if (y != p)
            DFS(y, x);
    }
}

inline int lca(int a, int b) {
    if (D[a] > D[b]) {
        swap(a, b);
    }
    for (int d = LGN - 1; d >= 0; d--)
        if (D[up[d][b]] >= D[a])
            b = up[d][b];
    if (a == b)
        return a;
    for (int d = LGN - 1; d >= 0; d--)
        if (up[d][a] != up[d][b])
            a = up[d][a], b = up[d][b];
    return up[0][a];
}

inline int dist(int a, int b) {
    int l = lca(a, b);
    return D[a] + D[b] - 2 * D[l];
}

bool infinite(int x) {
    for (int y : F[x]) {
        if (dist(x, y) >= 3)
            return true;
    }
    return false;
}

bool isinfinite = false;
int best = 0;

int s, t;


void DFS2(int x, int p = -1, int d = 0) {
    best = max(best, dist(t, x));
    if (infinite(x)) {
        isinfinite = true;
        return;
    }
    for (int y : F[x]) {
        if (y == p)
            continue;
        if (d + 1 < dist(t, y)) {
            DFS2(y, x, d + 1);
            if (isinfinite)
                return;
        }
    }
}

int main() {
    int n;
    scanf("%d %d %d", &n, &s, &t);
    --s, --t;
    for (int i = 0; i < n - 1; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        --a, --b;
        F[a].push_back(b);
        F[b].push_back(a);
    }
    for (int i = 0; i < n - 1; i++) {
        int a, b;
        scanf("%d %d", &a, &b);
        --a, --b;
        E[a].push_back(b);
        E[b].push_back(a);
    }
    DFS(0);
    DFS2(s);
    if (isinfinite) {
        printf("%d\n", -1);
    } else {
        printf("%d\n", 2 * best);
    }
}

