			hopp[t][i] = hopp[t-1][hopp[t-1][i]];
	}
	int jump(int a, int steps){
		for(int i = 0; steps; ++i){
			if(steps&1) a = hopp[i][a];
			steps /= 2;
		}
		return a;
	}
	int lca(int a, int b){
		if(h[a] > h[b]) swap(a,b);
		b = jump(b, h[b] - h[a]);
		if(a == b) return a;
		rrep(i,0,lg){
			int aa = hopp[i][a], bb = hopp[i][b];
			if(aa != bb){
				a = aa, b = bb;
			}
		}
		return par[b];
	}
	int dist(int a, int b){
		int c = lca(a,b);
		return h[a] + h[b] - 2*h[c];
	}
	int closer(int v, int y){
		if(v == y) return v;
		int r = lca(v, y);
		if(y != r) return par[y];
		return jump(v, h[v]-h[y]-1);
	}
};

