  for (int y : e[t][x])
    if (y != par) {
      dist[y] = dist[x] + 1;
      dfs(t, y, x);
    }
}

void solve(int x, int par, int d) {
  if (sp[x]) infi = true;
  sol = max(sol, 2 * dist[x]);
  for (int y : e[0][x]) {
    TRACE(x _ y _ d _ dist[y]);
    if (y != par && dist[y] > d + 1)
      solve(y, x, d + 1);
  }
}

int main(void) {
  scanf("%d %d %d",&n,&X,&Y);
  --X; --Y;
  REP(t, 2) REP(i, n - 1) {
    int a, b;
    scanf("%d %d",&a,&b);
    --a; --b;
    e[t][a].push_back(b);
    e[t][b].push_back(a);
  }

  dfs(1, Y, -1);

  REP(a, n)
    for (int b : e[0][a]) {
      bool lng = true;
      if (dad[a] == b) lng = false;
      if (dad[b] == a) lng = false;
      if (dad[a] == dad[b]) lng = false;
      if (dad[a] != -1 && dad[dad[a]] == b) lng = false;
      if (dad[b] != -1 && dad[dad[b]] == a) lng = false;
      if (lng) sp[a] = sp[b] = 1;
    }

  REP(x, n) TRACE(x _ sp[x]);

  solve(X, -1, 0);
  if (infi)
    sol = -1;

  printf("%d\n",sol);

  return 0;
}

#include<bits/stdc++.h>
using namespace std;
#define N 200005
const int INF=0x3f3f3f3f;
struct edge{
	int t,next;
}e[N*4];int ecnt,head[N*2];//1~n blue n+1~n+n red
void addedge(int f,int t){
	e[++ecnt]=(edge){t,head[f]};head[f]=ecnt;
	e[++ecnt]=(edge){f,head[t]};head[t]=ecnt;
}
int fa[N],dis[N];
void DFS(int u,int fr){
	fa[u]=fr;dis[u]=dis[fr]+1;
	for(int i=head[u];i;i=e[i].next)
		if(e[i].t!=fr) DFS(e[i].t,u);
} 
bool judge(int f,int t){//is the distance between f and t is smaller or equal to 2
	if(dis[f]==dis[t]) return fa[f]==fa[t];
	if(dis[f]>dis[t]) swap(f,t);
	return fa[t]==f||fa[fa[t]]==f;
}
int n,x,y,ans;
void DFS2(int u,int fr,int d){
	if(dis[u-n]<=d) return;
	ans=max(ans,dis[u-n]);
	for(int i=head[u];i;i=e[i].next)
		if(e[i].t!=fr){
			DFS2(e[i].t,u,d+1);
			if(judge(u-n,e[i].t-n)==false)
				ans=INF;
		}
}
int main(){
	scanf("%d%d%d",&n,&x,&y);
	for(int i=1,f,t;i<n;i++)
		scanf("%d%d",&f,&t),addedge(f+n,t+n);
	for(int i=1,f,t;i<n;i++)
		scanf("%d%d",&f,&t),addedge(f,t);
	dis[0]=-1;DFS(y,0);
	DFS2(x+n,0,0);
	if(ans==INF) ans=-1;
	else ans*=2;
	printf("%d\n",ans);
	return 0;
}

