    int a,b;
    scanf("%d%d",&a,&b);
    --a;--b;
    blue[a].push_back(b);
    blue[b].push_back(a);
  }
  // search win point
  REP(i,n)bluedepth[i] = 252521;
  bluedepth[y] = 0;
  blueparent[y] = -1;
  stack<int> S;
  S.push(y);
  while(!S.empty()){
    int p = S.top();S.pop();
    int d = bluedepth[p]+1;
    REP(i,blue[p].size()){
      int to = blue[p][i];
      if(bluedepth[to] < d)continue;
      blueparent[to] = p;
      bluedepth[to] = d;
