inline int lca(int x,int y){
  if(dpt[x]<dpt[y]) swap(x,y);
  for(int i=18;~i;i--)
    if(dpt[fa[x][i]]>=dpt[y]) x=fa[x][i];
  if(x==y) return x;
  for(int i=18;~i;i--)
    if(fa[x][i]!=fa[y][i]) x=fa[x][i],y=fa[y][i];
  return fa[x][0];
}

inline int dist(int x,int y){
  return dpt[x]+dpt[y]-2*dpt[lca(x,y)];
}

