void bfs(vvi& g, vi& d, int sv) {
  d[sv] = 0;
  queue<int> q;
  q.push(sv);
  while (sz(q)) {
    int v = q.front(); q.pop();
    for (int u : g[v]) {
      if (d[u] == INF) {
        d[u] = d[v]+1;
        q.push(u);
      }
    }
  }
}
void cfs(vvi& g, vi& d, int sv) {
  if (d[sv] != 1) return;
  d[sv] = 2;
  queue<int> q;
  q.push(sv);
  while (sz(q)) {
    int v = q.front(); q.pop();
    for (int u : g[v]) {
      if (d[u] == 1) {
        d[u] = 2;
        q.push(u);
      }
    }
  }
}

