        dfs(v, u);
    }
}
int lca(int u, int v) {
    int res = 0;
    if(depB[u]<depB[v]) swap(u,v);
    for(int i=mlog;i>=0;i--) {
        if(par[u][i] && depB[par[u][i]]>=depB[v]) {
            u = par[u][i];
            res += (1<<i);
        }
    }
    if(u==v) return res;
    for(int i=mlog;i>=0;i--) {
        if(par[u][i]!=par[v][i]) {
            u = par[u][i]; v = par[v][i];
            res += (1<<i)*2;
        }
    }
    return res+2;
}
