const int N=1000000+10;

struct Tree {
    struct edge { int v,nxt; } e[N<<1];
    int head[N];
    inline void addEdge(int u,int v) {
        static int cnt=0;
        e[++cnt]=(edge){v,head[u]},head[u]=cnt;
    }

    int dep[N],sz[N],fa[N],hson[N],top[N];
    inline void dfs1(int u,int f) {
        dep[u]=dep[fa[u]=f]+1,sz[u]=1;
        for (re int i=head[u];i;i=e[i].nxt) {
            int v=e[i].v; if (v==f) continue;
            dfs1(v,u),sz[u]+=sz[v];
            if (sz[v]>sz[hson[u]]) hson[u]=v;
        }
    }
    inline void dfs2(int u,int anc) {
        top[u]=anc;
        if (hson[u]) dfs2(hson[u],anc);
        for (re int i=head[u];i;i=e[i].nxt)
            if (e[i].v!=fa[u]&&e[i].v!=hson[u]) dfs2(e[i].v,e[i].v);
    }

    inline int getlca(int u,int v) {
        while (top[u]!=top[v]) {
            if (dep[top[u]]<dep[top[v]]) swap(u,v);
            u=fa[top[u]];
        }
        return dep[u]<dep[v]?u:v;
    }
    inline int getdis(int u,int v) {
        int t=getlca(u,v);
        return dep[u]+dep[v]-(dep[t]<<1);
    }
} A,B;

int n,x,y,vis[N];

inline void bfs() {
    queue<int> Q; vis[x]=1,Q.push(x);
    while (!Q.empty()) {
        int u=Q.front(); Q.pop();
        for (re int i=A.head[u];i;i=A.e[i].nxt) {
            int v=A.e[i].v; if (vis[v]) continue;
            if (A.dep[v]<B.dep[v]) vis[v]=1,Q.push(v);
        }
    }
}

