inline void lca(int x,int y,int &i,int &j)
{
	int a=x,b=y,k;
	if(h[a]>h[b])
	  {
	   for(k=20;k>=0;k--)
	     if(h[a]-(1<<k)>=h[b])
	       a=f[a][k];
	   if(a==b)
	     {
		  i=f[x][0];
		  j=h[x]-h[y]-1;
		  return;
		 }
	  }
	if(h[a]<h[b])
	  {
	   for(k=20;k>=0;k--)
	     if(h[b]-(1<<k)>h[a])
	       b=f[b][k];
	   if(f[b][0]==a)
	     {
		  i=b;
		  j=h[y]-h[x]-1;
		  return;
		 }
	   b=f[b][0];
	  }
	for(k=20;k>=0;k--)
	  if(f[a][k]!=f[b][k])
	    a=f[a][k],b=f[b][k];
	i=f[x][0];
	j=h[x]+h[y]-h[a]-h[b]+1;
}
