#include <bits/stdc++.h>
using namespace std;
const int N=200005;
int read(){
	int x=0;
	char ch=getchar();
	while (!isdigit(ch))
		ch=getchar();
	while (isdigit(ch))
		x=(x<<1)+(x<<3)+(ch^48),ch=getchar();
	return x;
}
int n,X,Y;
vector <int> er[N],eb[N];
int depth[N],fa[N][20];
int tag[N];
void dfs1(int x,int pre,int d){
	depth[x]=d,fa[x][0]=pre;
	for (int i=1;i<20;i++)
		fa[x][i]=fa[fa[x][i-1]][i-1];
	for (auto y : eb[x])
		if (y!=pre)
			dfs1(y,x,d+1);
}
int LCA(int x,int y){
	if (depth[x]<depth[y])
		swap(x,y);
	for (int i=19;i>=0;i--)
		if (depth[x]-(1<<i)>=depth[y])
			x=fa[x][i];
	if (x==y)
		return x;
	for (int i=19;i>=0;i--)
		if (fa[x][i]!=fa[y][i])
			x=fa[x][i],y=fa[y][i];
	return fa[x][0];
}
int Dis(int x,int y){
	return depth[x]+depth[y]-2*depth[LCA(x,y)];
}
int vis[N],ans=0;
void dfs(int x,int d){
	vis[x]=1;
	int k1=Dis(x,Y);
	if (d>=k1){
		ans=max(ans,d*2-(d>k1));
		return;
	}
	if (tag[x]){
		ans=max(ans,10000000);
		return;
	}
	ans=max(ans,k1*2);
	for (auto y : er[x])
		if (!vis[y])
			dfs(y,d+1);
}
int main(){
	n=read(),X=read(),Y=read();
	for (int i=1;i<=n;i++)
		er[i].clear(),eb[i].clear();
	for (int i=1;i<n;i++){
		int a=read(),b=read();
		er[a].push_back(b);
		er[b].push_back(a);
	}
	for (int i=1;i<n;i++){
		int a=read(),b=read();
		eb[a].push_back(b);
		eb[b].push_back(a);
	}
	dfs1(1,0,0);
	memset(tag,0,sizeof tag);
	memset(vis,0,sizeof vis);
	for (int x=1;x<=n;x++)
		for (auto y : er[x])
			if (Dis(x,y)>2)
				tag[x]=tag[y]=1;
	dfs(X,0);
	printf("%d",ans<2e6?ans:-1);
	return 0;
}

#include <cstdio>
#include <cstring>
#include <algorithm>
#define MAXN 200010
using namespace std;

struct edge{
	int to,next;
	edge(int _to=0,int _next=0):to(_to),next(_next){}
}e[MAXN<<1],e2[MAXN<<1];

int n,x1,x2;
int g[MAXN],nume;
int g2[MAXN],nume2;
int dep[MAXN],size[MAXN],son[MAXN],top[MAXN],pre[MAXN],w[MAXN],pt[MAXN],numw;
int pre2[MAXN],h[MAXN],dep2[MAXN];
bool flag;
int ans;

void addEdge(int u,int v){
	e[nume]=edge(v,g[u]);
	g[u]=nume++;
}

void addEdge2(int u,int v){
	e2[nume2]=edge(v,g2[u]);
	g2[u]=nume2++;
}

void dfs(int x,int p){
	size[x]=1; son[x]=0;
	pre[x]=p;
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to^p){
			dep[e[i].to]=dep[x]+1;
			dfs(e[i].to,x);
			size[x]+=size[e[i].to];
			if(size[e[i].to]>size[son[x]])
				son[x]=e[i].to;
		}
}

void dfs2(int x,int p){
	pt[w[x]=++numw]=x;
	if(son[x]){
		top[son[x]]=top[x];
		dfs2(son[x],x);
	}
	for(int i=g[x];~i;i=e[i].next)
		if(e[i].to!=p && e[i].to!=son[x]){
			top[e[i].to]=e[i].to;
			dfs2(e[i].to,x);
		}
}

int getLCA(int x,int y){
	while(top[x]^top[y]){
		if(dep[top[x]]<dep[top[y]]) swap(x,y);
		x=pre[top[x]];
	}
	return dep[x]<dep[y]?x:y;
}

int getDis(int x,int y){
	return dep[x]+dep[y]-2*dep[getLCA(x,y)];
}

int goUp(int x,int d){
	while(dep[x]-dep[top[x]]+1<d){
		d-=dep[x]-dep[top[x]]+1;
		x=pre[top[x]];
	}
	return pt[w[x]-d];
}

int step(int x,int y){
	int lca=getLCA(x,y);
	if(lca!=x) return pre[x];
	return goUp(y,dep[y]-dep[x]-1);
}

void dfs3(int x,int p){
	pre2[x]=p;
	for(int i=g2[x];~i;i=e2[i].next)
		if(e2[i].to^p){
			int y=e2[i].to;
			int dis=getDis(x,y);
			if(dis>=3) flag=1;
			if(y==h[x] || x==h[x]) continue;
			h[y]=step(h[x],y);
			dep2[y]=dep2[x]+1;
			dfs3(y,x);
		}
	int res=getDis(h[x],x)*2+dep2[x]*2;
	ans=max(ans,res);
}

int main(){
#ifdef DEBUG
	freopen("E.in","r",stdin);
#endif
	memset(g,-1,sizeof g);
	memset(g2,-1,sizeof g2);
	scanf("%d%d%d",&n,&x1,&x2);
	for(int i=1;i<n;i++){
		int u,v;
		scanf("%d%d",&u,&v);
		addEdge2(u,v);
		addEdge2(v,u);
	}
	for(int i=1;i<n;i++){
		int u,v;
		scanf("%d%d",&u,&v);
		addEdge(u,v);
		addEdge(v,u);
	}
	dfs(1,0);
	top[1]=1;
	dfs2(1,0);
	h[x1]=x2;
	dfs3(x1,0);
	if(flag) puts("-1");
	else printf("%d\n",ans);
	return 0;
}

