inline bool upmax (T &a, const S &b) { return a < b ? a = b, 1 : 0; }


ull twop (ll x) { return 1ULL<<x; }

ll MOD (ll a, ll m) {
    a %= m;
    if (a < 0)a += m;
    return a;
}

ll inverse (ll a, ll m) {
    a = MOD (a, m);
    if (a <= 1)return a;
    return MOD ((1 - inverse (m, a) * m) / a, m);
}

template<typename T>
T sqr (T x) { return x * x; }

ll gcd (ll a, ll b) {
    a = abs (a), b = abs (b);
    while (b != 0) {
        a %= b;
        swap (a, b);
    }
    return a;
}

ll fast (ll a, ll b) {
    ll ans = 1;
    while (b) {
        if (b & 1)ans *= a;
        a *= a;
        b /= 2;
    }
    return ans;
}

ll fast (ll a, ll b, ll mod) {
    if (b < 0)a = inverse (a, mod), b = -b;
    ll ans = 1;
    while (b) {
        if (b & 1)ans = ans * a % mod;
        a = a * a % mod;
        b /= 2;
    }
    return ans % mod;
}


namespace SOLVE {
    const ll N = 200010;
    VLL red[N], blue[N];
    ll n, x, y;
    int par[N];
    int dep[N];

    void dfs (int cur, int fa) {
        par[cur] = fa;
        for (auto s:blue[cur]) {
            if (s != fa) {
                dep[s] = dep[cur] + 1;
                dfs (s, cur);
            }
        }
    }

    bool niubi (int x, int y) {
        REP(i, 0, 2) {
            if (par[x] == par[y])return false;
            if (par[par[x]] == y)return false;
            if (par[x] == y)return false;
            swap (x, y);
        }
        return true;
    }

    void bfs () {
        queue<int> q;
        q.push (x);
        VI vis (n + 1, 0);
        VI dis (n + 1, 0);
        vis[x] = 1;
        int ans = dep[x]*2;
        while (sz(q)) {
            auto f = q.front ();
            q.pop ();
            for (auto s:red[f]) {
                if (!vis[s]) {
                    dis[s] = dis[f] + 1;
                    vis[s] = 1;
                    if (dis[s] < dep[s]) {
                        q.push (s);
                        if (niubi (s, f)) {
                            cout<<"-1";
                            return;
                        }
                    }
                    if (dis[s] <= dep[s]) {
                        dbg(s,dis[s]);
                        upmax (ans, 2 * dep[s]);
                    }
                }
            }
        }
        cout<<ans;
    }

