		for(int i=1;i<n;i++){
			SF("%d%d",&u,&v);
			l[i]=make_pair(u,v);
			a[u].push_back(v);
			a[v].push_back(u);
		}
		for(int i=1;i<n;i++){
			SF("%d%d",&u,&v);
			b[u].push_back(v);
			b[v].push_back(u);	
		}
		dfsA(x,0);
		dfsB(y,0);
		ans=0;
		dfs(x,0);
		PF("%d\n",ans);
		for(int i=1;i<=n;i++){
			a[i].clear();
			b[i].clear();
		}
	}
}

