void DFSA(int o, int f = 0){
	for(int i = A.Begin[o]; i; i = A.Next[i]){
		int u = A.to[i];
		if(u == f) continue;
		depa[u] = depa[o] + 1;
		fa[u] = o;
		DFSA(u, o);
	}
}

bool check(int u, int v){
	int d = 0;
	if(depa[u] < depa[v]) swap(u, v);
	while(depa[u] > depa[v] && d < 3) u = fa[u], ++d;
	while(u != v && d < 3) u = fa[u], v = fa[v], d += 2;
	return d > 2;
}

int ans;

void DFSB(int o, int f = 0){
	ans = max(ans, depa[o]);
	vis[o] = true;
	for(int i = B.Begin[o]; i; i = B.Next[i]){
		int u = B.to[i];
		if(u == f) continue;
		depb[u] = depb[o] + 1;
		if(depa[u] <= depb[u]) continue;
		DFSB(u, o);
	}
}

