void dfsred(ll a,ll p=-1,ll H=0){
    stred[a]=tt++;
    minred[a]=H;
    for(auto v:red[a]){
        if(v!=p){
            parred[v][0]=a;
            dfsred(v,a,H+1);
        }
    }
    etred[a]=tt++;
}
void dfsblu(ll a,ll p=-1,ll H=0){
    stblu[a]=tt++;
    minblu[a]=H;
    for(auto v:blu[a]){
        if(v!=p){
            parblu[v][0]=a;
            dfsblu(v,a,H+1);
        }
    }
    etblu[a]=tt++;
}
bool is_par_red(ll a,ll b){
    return (stred[a]<=stred[b] &&etred[a]>=etred[b]);
}
bool is_par_blu(ll a,ll b){
    return (stblu[a]<=stblu[b] &&etblu[a]>=etblu[b]);
}
ll lca_red(ll a,ll b){
    if(is_par_red(a,b))return a;
    if(is_par_red(b,a))return b;
    for(ll i=19;i>=0;i--){
        if(!is_par_red(parred[a][i],b))
            a=parred[a][i];
    }
    return parred[a][0];
}
ll lca_blu(ll a,ll b){
    if(is_par_blu(a,b))return a;
    if(is_par_blu(b,a))return b;
    for(ll i=19;i>=0;i--){
        if(!is_par_blu(parblu[a][i],b))
            a=parblu[a][i];
    }
    return parblu[a][0];
}
ll fas_red(ll a,ll b){
    ll lc=lca_red(a,b);
    return minred[a]+minred[b]-2*minred[lc];
}
ll fas_blu(ll a,ll b){
    ll lc=lca_blu(a,b);
    return minblu[a]+minblu[b]-2*minblu[lc];
}
ll nazdik_blu(ll a,ll b){
    if(a==b)return a;
    if(!is_par_blu(b,a)){
        return parblu[b][0];
    }
    for(ll i=19;i>=0;i--){
        if(!is_par_blu(parblu[a][i],b))
            a=parblu[a][i];
    }
    return a;
}
void dfs_mishe(ll a,ll b,ll p=-1){
    if(a==b){
