    }
    ans=max(ans,minred[a]*2+fas_blu(a,b)*2);
    mishe[a]=1;
    for(auto v:red[a]){
        if(v!=p){
            dfs_mishe(v,nazdik_blu(v,b),a);
        }
    }
}
