	for(int i=1;i<n;++i) ins(h2,read(),read());
	Bfs(d1,h1,X);Bfs(d2,h2,Y);Pre(Y,0);
	for(int i=1;i<=MD;++i) for(int j=1;j<=n;++j) fa[i][j]=fa[i-1][fa[i-1][j]];
	dfs(X,0);
	if(flag) puts("-1");
	else printf("%d\n",ans*2);
	return 0;
}

