int n;
void yaru(int root) {
	dfs(root, -1);
	rep(j, 17) {
		rep(i, n) {
			if (par[i][j] < 0)par[i][j + 1] = -1;
			else par[i][j + 1] = par[par[i][j]][j];
		}
	}
}
int lca(int a, int b) {
	if (depth[a] < depth[b])swap(a, b);
	rep(k, 18) {
		if ((depth[a] - depth[b]) >> k & 1) {
			a = par[a][k];
		}
	}
	if (a == b)return a;
	per(k, 18) {
		if (par[a][k] != par[b][k]) {
			a = par[a][k], b = par[b][k];
		}
	}
	return par[a][0];
}
int dist(int a, int b) {
	int l = lca(a, b);
	return depth[a] + depth[b] - 2 * depth[l];
}

