		}
		else {
			g[l].push_back(r);
			g[r].push_back(l);
		}
	}
	fill(d, d + n, mod);
	d[x] = 0;
	queue<int> q; q.push(x);
	while (!q.empty()) {
		int id = q.front(); q.pop();
		for (int to : g[id]) {
			int nd = d[id] + 1;
			if (nd < d[to] && nd < depth[to]) {
