int disR[N];
int disB[N];


void dfs(int v = 0, int p = -1)
{
    for (int u : blue[v])
    {
        if (u != p)
        {
            par[0][u] = v;
            h[u] = h[v] + 1;
            dfs(u, v);
        }
    }
}

void pre()
{
    for (int k = 1; k < lg; k++)
    {
        for (int i = 0; i < n; i++)
        {
            par[k][i] = par[k - 1][ par[k - 1][i] ];
        }
    }
}

int lca(int v, int u)
{
    if(h[v] > h[u])
    {
        swap(v, u);
    }
    for (int i = 0; i < lg; i++)
    {
        if (h[u] - h[v] >> i & 1)
        {
            u = par[i][u];
        }
    }
    for (int i = lg - 1; i >= 0; i--)
    {
        if(par[i][v] != par[i][u])
        {
            v = par[i][v], u = par[i][u];
        }
    }
    return v == u ? v : par[0][v];
}

void dfs1(int v, int par = -1)
{
    if (disR[v] >= disB[v])
    {
        return;
    }
    for (auto u: red[v])
    {
        if (u != par)
        {
            disR[u] = disR[v] + 1;
            dfs1(u, v);
        }
    }
}

void dfs2(int v, int par = -1)
{
    for (auto u: red[v])
    {
        if (u != par)
        {
            disB[u] = disB[v] + 1;
            dfs2(u, v);
        }
    }
}



