vector<int> gen_parent(int N, int root, map<int, vector<int>> &to)
{
    vector<int> res(N, -1);
    queue<pint> q;
    q.push(pint(root, -1));
    while (!q.empty())
    {
        pint pa = q.front();
        q.pop();
        for (auto nxt : to[pa.first]) if (nxt != pa.second)
        {
            res[nxt] = pa.first;
            q.push(pint(nxt, pa.first));
        }
    }
    return res;
}

// 
vector<int> generation(int N, int root, map<int, vector<int>> &to)
{
    vector<int> res(N); // res[root] = 0;
    queue<pint> q;
    q.push(pint(root, -1));
    while(!q.empty())
    {
        pint pa = q.front();
        q.pop();
        if (pa.second >= 0) res[pa.first] = res[pa.second] + 1;
        for (auto nxt : to[pa.first]) if (nxt != pa.second) q.push(pint(nxt, pa.first));
    }
    return res;
}

vector<vector<int>> doubling_parent(const vector<int> &par)
{
    int V = par.size();
    int lnV = 1;
    while ((1 << (lnV - 1)) < V) lnV++;

    vector<vector<int>> res(lnV, vector<int>(V, -1));
    res[0] = par;
    FOR(d, 1, lnV) REP(i, V) if (res[d - 1][i] >= 0) res[d][i] = res[d - 1][res[d - 1][i]];
    return res;
}

pint lowest_common_ancestor(int s, int t, const vector<int> &gen, const vector<vector<int>> &doubling_par)
{
    pint res;
    int d = 0, diff = gen[s] - gen[t];
    if (diff < 0)
    {
        res = lowest_common_ancestor(t, s, gen, doubling_par);
        return pint(res.second, res.first);
    }
    res.first = diff;
    while (diff)
    {
        if (diff & 1) s = doubling_par[d][s];
        d++, diff >>= 1;
    }

    if (s != t)
    {
        int oya = 1;
        IREP(d, doubling_par.size()) if (doubling_par[d][s] != doubling_par[d][t])
        {
            oya += 1 << d;
            s = doubling_par[d][s], t = doubling_par[d][t];
        }
        res.first += oya;
        res.second += oya;
    }
    return res;
}

int nth_parent(int now, int k, const vector<vector<int>> &doubling_par)
{
    int d = 0;
    while (k)
    {
        if (k & 1) now = doubling_par[d][now];
        k >>= 1; d++;
    }
    return now;
}

