// LCA (0-indexed)
struct LCA {
  int n, bi;
  vi dep;
  vector<vi> to, par;
  LCA(int n):n(n),dep(n),to(n){}
  void add(int a, int b){ to[a].pb(b); to[b].pb(a);}
  void calcDep(int v, int ndep=0, int p=-1){
    dep[v] = ndep; par[0][v] = p;
    for(int u : to[v]) if(u != p) calcDep(u,ndep+1,v);
  }
  void init(){
    bi = 0;
    while(1<<bi <= n) bi++;
    par.resize(bi,vi(n,-1));
    calcDep(0);
    rep(i,bi-1)rep(j,n) par[i+1][j] = (par[i][j]==-1?-1:par[i][par[i][j]]);
  }
  int lca(int a, int b){
    if(dep[a] < dep[b]) swap(a,b);
    int x = dep[a]-dep[b];
    for(int i = bi-1; i >= 0; --i){
      if(1<<i <= x) a = par[i][a], x -= 1<<i;
    }
    if(a == b) return a;
    for(int i = bi-1; i >= 0; --i){
      if(par[i][a] != par[i][b]) a = par[i][a], b = par[i][b];
    }
    return par[0][a];
  }
  int dist(int a, int b){
    int c = lca(a,b);
    return dep[a]+dep[b] - dep[c]*2;
  }
};
