}
struct tree{
	int hea[maxn],nxt[maxn<<1],to[maxn<<1],tot;
	void add(int x,int y)
	{
		nxt[++tot]=hea[x];
		hea[x]=tot;
		to[tot]=y;
	}
}T[2];
int dep[maxn][2],jump[maxn][20],maxx;
void dfs(int now,int fa,int opt)
{
	dep[now][opt]=dep[fa][opt]+1;
	if(opt==1)jump[now][0]=fa;
	for(int i=T[opt].hea[now];i;i=T[opt].nxt[i])
	{
		int to=T[opt].to[i];
		if(to==fa)continue;
		dfs(to,now,opt);
	}
}
int lca(int x,int y)
{
	if(dep[x][1]<dep[y][1])swap(x,y);
	for(int i=19;i>=0;i--)
		if(dep[jump[x][i]][1]>=dep[y][1])
			x=jump[x][i];
	if(x==y)return x;
	for(int i=19;i>=0;i--)
		if(jump[x][i]!=jump[y][i])
			x=jump[x][i],y=jump[y][i];
	return jump[x][0];
}
void getans(int now,int fa)
{
	if(fa)
	{
		int p=lca(now,fa);
		if(dep[now][1]+dep[fa][1]-2*dep[p][1]>2)
		{
			puts("-1");
			exit(0);
		}
	}
	if(dep[now][0]>=dep[now][1])return;
	maxx=max(maxx,dep[now][1]);
	for(int i=T[0].hea[now];i;i=T[0].nxt[i])
	{
		int y=T[0].to[i];
		if(y==fa)continue;
		getans(y,now);
	}
}
int main()
{
	int n=read(),x=read(),y=read(),s,t;
	for(int ii=0;ii<2;ii++)
		for(int i=1;i<n;i++)
		{
			s=read(),t=read();
			T[ii].add(s,t);
			T[ii].add(t,s);
