	dfs(x,0,0),dfs(y,0,1);
	for(int i=1;i<20;i++)
		for(int j=1;j<=n;j++)
			jump[j][i]=jump[jump[j][i-1]][i-1];
	getans(x,0);
	printf("%d\n",2*maxx-2);
	return 0;
}

