class LCA{
	public:
	int N,root,max_log;
	vvi G,parent;
	vi depth;
	LCA(int size){
		N=size;
		G=vvi(size);
		max_log=20;
		depth=vi(size);
		parent=vvi(max_log,vi(size));
	}
	void add_edge(int a,int b){
		G[a].pb(b);
		G[b].pb(a);
	}
	void dfs(int v,int p,int d){
		parent[0][v]=p;
		depth[v]=d;
		rep(i,G[v].size())if(G[v][i]!=p)dfs(G[v][i],v,d+1);
	}
	void init(){
		root=0;
		dfs(root,-1,0);
		for(int k=0;k+1<max_log;k++){//doubling
			for(int v=0;v<N;v++){
				if(parent[k][v]<0)parent[k+1][v]=-1;
				else parent[k+1][v]=parent[k][parent[k][v]];
			}
		}
	}
	int lca(int u,int v){
		int out=depth[u]+depth[v];
		if(depth[u]>depth[v])swap(u,v);
		rep(k,max_log)if((depth[v]-depth[u])>>k&1)v=parent[k][v];
		if(u==v){
			out-=2*depth[u];
			return out;
		}
		for(int k=max_log-1;k>=0;k--){
			if(parent[k][u]!=parent[k][v]){
				u=parent[k][u];
				v=parent[k][v];
			}
		}
		out-=2*depth[parent[0][u]];
		return out;
	}
};
int n,x,y,out;
vvi G,tG;
vi co,h;
void dfs1(int a,int p,int c){
	co[a]=c;
	rep(i,tG[a].size()){
		int to=tG[a][i];
		if(to==p)continue;
		dfs1(to,a,c+1);
	}
}
void dfs2(int a,int p,int c){
	if(c>=co[a])return;
	if(h[a])out=inf;
	cmax(out,2*co[a]);
	rep(i,G[a].size()){
		int to=G[a][i];
		if(to==p)continue;
		dfs2(to,a,c+1);
	}
}
signed main(){
	cin>>n>>x>>y;
	G=tG=vvi(n);
	co=h=vi(n);
	LCA lca(n);
	rep(i,n-1){
		int a,b;
		cin>>a>>b;
		a--;b--;
		lca.add_edge(a,b);
		G[a].pb(b);
		G[b].pb(a);
	}
	lca.init();
	rep(i,n-1){
		int a,b;
		cin>>a>>b;
		a--;b--;
		tG[a].pb(b);
		tG[b].pb(a);
		if(lca.lca(a,b)>=3)h[a]=h[b]=1;
	}
	dfs1(y-1,-1,0);
	dfs2(x-1,-1,0);
	if(out==inf)cout<<-1<<endl;
	else cout<<out<<endl;
}

