const ll maxn = 200010;
const ll maxnlg = 19;

ll n, X, Y;
pii es_a[maxn];
vi g[2][maxn];
ll st[maxn], ft[maxn], pars[maxnlg][maxn], ht[maxn];
ll q[maxn * 2], col[maxn];
bool vis[maxn], vis2[maxn];

ll tim = 0;
void dfs_lca(ll x, ll par) {
    st[x] = tim++;
    pars[0][x] = par;
    fori (i, maxnlg - 1) pars[i + 1][x] = pars[i][pars[i][x]];
    for (auto y : g[1][x]) {
        if (y != par) {
            ht[y] = ht[x] + 1;
            dfs_lca(y, x);
        }
    }
    ft[x] = tim;
}

ll LCA(ll a, ll b) {
    if (st[a] > st[b]) swap(a, b);
    if (ft[a] >= ft[b]) return a;
    forir (i, maxnlg) {
        if (ft[pars[i][a]] < ft[b]) a = pars[i][a];
    }
    return pars[0][a];
}

void MAIN() {

    cin >> n >> X >> Y; --X, --Y;
    fori (i, n - 1) {
        ll a, b; cin >> a >> b; --a, --b;
        es_a[i] = {a, b};
        g[0][a].eb(b), g[0][b].eb(a);
    }
    fori (i, n - 1) {
        ll a, b; cin >> a >> b; --a, --b;
        g[1][a].eb(b), g[1][b].eb(a);
    }

    dfs_lca(Y, Y);

    memset(col, -1, sizeof col);
    ll l = 0, r = 0;
    q[r++] = Y;
    q[r++] = X;
    vis[X] = true;
    col[X] = 0;
    col[Y] = 1;
    while (r != l) {
        ll x = q[l++];
        for (auto y : g[col[x]][x]) {
            if (col[y] == -1 || col[x] == 1 && col[y] != 1) {
                if (col[x] == 0) vis[y] = true;
                col[y] = col[x];
                q[r++] = y;
            }
        }
    }

    memset(col, -1, sizeof col);
    l = 0, r = 0;
    q[r++] = X;
    q[r++] = Y;
    vis2[X] = true;
    col[X] = 0;
    col[Y] = 1;
    while (r != l) {
        ll x = q[l++];
        for (auto y : g[col[x]][x]) {
            if (col[y] == -1 || col[x] == 1 && col[y] != 1) {
                if (col[x] == 0) vis2[y] = true;
                col[y] = col[x];
                q[r++] = y;
            }
        }
    }

    fori (i, n - 1) {
        ll a, b; tie(a, b) = es_a[i];
        if ((vis[a] || vis[b]) && ht[a] + ht[b] - ht[LCA(a, b)] * 2 > 2) {
            cout << "-1\n";
            return;
        }
    }
    ll ans = 0;
    fori (i, n) {
        if (vis2[i]) smax(ans, ht[i]);
    }
    cout << ans * 2 << '\n';

}

