void dfs(int u, int f = 0) {
    fa[u][0] = f;
    dep[u] = dep[f] + 1;
    for(int i = 1; i <= 20; ++i) fa[u][i] = fa[fa[u][i-1]][i-1];
    for(int i = 0; i < int(G[u].size()); ++i) if(G[u][i] != f) { dfs(G[u][i], u); }
}
int lca(int u, int v) {
    if(dep[u] < dep[v]) swap(u, v);
    for(int i = 20; i >= 0; --i) 
        if(dep[fa[u][i]] >= dep[v]) u = fa[u][i];

    if(u == v) return u;
    for(int i = 20; i >= 0; --i) if(fa[u][i] ^ fa[v][i])
        u = fa[u][i], v = fa[v][i];
    return fa[u][0];
}
inline int dis(int u, int v) { return dep[u] + dep[v] - (dep[lca(u, v)] << 1); }

