    int k = Q.front();
    for (int x : gb[k]) {
      if (!vis[x]) {
        Q.emplace_back(x);
        vis[x] = true;
        dep[x] = dep[k] + 1;
        fa[x] = k;
      }
    }
    Q.pop_front();
