void f1(int w,int pa){
	for(int i=0;i<e2[w].size();++i){
		int u=e2[w][i];
		if(u!=pa){
			dep[u]=dep[w]+2;
			fa[u]=w;
			f1(u,w);
		}
	}
}
void f2(int w,int pa,int d){
	if(d>dep[w])return;
	if(un[w])ans=-1;
	if(ans!=-1&&ans<dep[w])ans=dep[w];
	for(int i=0;i<e1[w].size();++i){
		int u=e1[w][i];
		if(u!=pa)f2(u,w,d+2);
	}
}
