struct edge { int to,from,v; }e[N*4];

void insert(int x,int y,int v) 
{
	e[++cnt].to=y,e[cnt].from=head[x],head[x]=cnt,e[cnt].v=v;
	e[++cnt].to=x,e[cnt].from=head[y],head[y]=cnt,e[cnt].v=v;
}
void dfs(int x)
{
	dep[x]=dep[fa[x]]+1,be[x]=++tot;
	for (int i=head[x];i;i=e[i].from) if (e[i].to!=fa[x]) fa[e[i].to]=x,dfs(e[i].to);
	ed[x]=++tot;
}
void bfs()
{
	int l=1,r=1; Q[1]=X,vis[X]=1;
	while (l<=r)
	{
		int x=Q[l++];
		for (int i=head[x];i;i=e[i].from) if (!e[i].v&&!vis[e[i].to])
		{
			dis[e[i].to]=dis[x]+1;
			if (dis[e[i].to]<dep[e[i].to]) vis[e[i].to]=1,Q[++r]=e[i].to;
		}
	}
}
bool pd(int x,int y)
{
	if (be[x]>be[y]) swap(x,y);
	if (be[x]<be[y]&&ed[x]>ed[y]) return dep[y]-dep[x]>2?true:false;
	if (fa[x]==fa[y]) return false;
	return true;
}
