const int maxn=200010;
int n,A,B;
int head[maxn],nxt[maxn<<1],to[maxn<<1],e;
int head2[maxn],nxt2[maxn<<1],to2[maxn<<1],e2;
void ae(int x,int y){
    to[++e]=y; nxt[e]=head[x]; head[x]=e;
}
void ae2(int x,int y){
    to2[++e2]=y; nxt2[e2]=head2[x]; head2[x]=e2;
}
int fa[maxn][20],dep[maxn];
void dfs(int u){
    for(int i=1;i<20;i++) fa[u][i]=fa[fa[u][i-1]][i-1];
    for(int i=head[u];i;i=nxt[i]){
        int v=to[i];
        if(v!=fa[u][0]){
            fa[v][0]=u; dep[v]=dep[u]+1; dfs(v);
        }
    }
}
int lca(int u,int v){
    if(dep[u]<dep[v]) swap(u,v);
    for(int i=19;i>=0;i--) if(dep[fa[u][i]]>=dep[v]) u=fa[u][i];
    if(u==v) return u;
    for(int i=19;i>=0;i--) if(fa[u][i]!=fa[v][i]) u=fa[u][i],v=fa[v][i];
    return fa[u][0];
}
