#include <bits/stdc++.h>

const int N = 200005;
int n, x, y;

int a[N], b[N], c[N], d[N];
struct tree {
	int head[N], tot;
	struct edge { int to, nxt; } e[N << 1];
	void link(int x, int y) {
		e[++tot] = (edge) {y, head[x]}, head[x] = tot;
		e[++tot] = (edge) {x, head[y]}, head[y] = tot;
	}
} R, B;
bool mark[N];

int depB[N], fB[N];
void dfs0(int x) {
	static auto head = B.head;
	static auto e = B.e;
	for (int i = head[x]; i; i = e[i].nxt)
		if (e[i].to != fB[x])
			fB[e[i].to] = x, depB[e[i].to] = depB[x] + 1, dfs0(e[i].to);
}
int ans = 0;
void dfs1(int x, int f = 0, int dep = 0) {
	if (dep >= depB[x]) return;
	if (mark[x]) std::cout << -1 << '\n', std::exit(0);
	ans = std::max(ans, depB[x]);
	static auto head = R.head;
	static auto e = R.e;
	for (int i = head[x]; i; i = e[i].nxt)
		if (depB[e[i].to] > dep) dfs1(e[i].to, x, dep + 1);
}

bool qaq(int x, int y) {
	if (depB[x] > depB[y]) std::swap(x, y);
	auto f = fB;
	return f[y] == f[x] || f[y] == x || f[f[y]] == x;
}

int main() {
	std::ios::sync_with_stdio(0), std::cin.tie(0);
	std::cin >> n >> x >> y;
	for (int i = 1; i < n; ++i)
		std::cin >> a[i] >> b[i], R.link(a[i], b[i]);
	for (int i = 1; i < n; ++i)
		std::cin >> c[i] >> d[i], B.link(c[i], d[i]);
	dfs0(y);
	for (int i = 1; i < n; ++i)
		if (!qaq(a[i], b[i])) mark[a[i]] = mark[b[i]] = 1;
	dfs1(x), std::cout << ans * 2 << '\n';
	return 0;
}

