using pii = pair <int , int> ;

int const N = 2e5 + 20 ;
int n , x , y , ans ;

int hr[N] , parr[N] ;
int hb[N] , parb[N] ;

bool spec[N] ;

vector <int> gr[N] , gb[N] ;

void dfs (int v , vector <int> g[N] , int h[N] , int par[N]) {
	for (int u : g[v])
		if (u != par[v]) {
			par[u] = v ;
			h[u] = h[v] + 1 ;
			dfs(u , g , h , par) ;
		}
}

inline bool ok (int u , int v) {
	if (hb[u] > hb[v]) swap(u , v) ;

	if (hb[v] - hb[u] > 2) return 0 ;
	if (hb[v] - hb[u] == 2) return parb[parb[v]] == u ;
	if (hb[v] - hb[u] == 1) return parb[v] == u ;
	return parb[v] == parb[u] ;
}

void dfs_calc (int v) {
	if (hb[v] <= hr[v]) return ;

	if (spec[v]) {
		cout << "-1\n" ;
		exit(0) ;
	}
	ans = max(ans , 2 * hb[v]) ;

	for (int u : gr[v])
		if (u != parr[v]) dfs_calc(u) ;
}

int main(){
	ios::sync_with_stdio(false) , cin.tie(0) , cout.tie(0) ;

	cin >> n >> x >> y ;
	x -- , y -- ;

	for (int i = 0 ; i < 2 * n - 2 ; i ++) {
		int u , v ;
		cin >> u >> v ;
		u -- , v -- ;
		(i < n - 1 ? gr : gb)[u].push_back(v) ;
		(i < n - 1 ? gr : gb)[v].push_back(u) ;
	}

	memset(parr , -1 , sizeof parr) ;
	dfs(x , gr , hr , parr) ;
	memset(parb , -1 , sizeof parb) ;
	dfs(y , gb , hb , parb) ;

	for (int v = 0 ; v < n ; v ++)
