void dfsy(int u) {
    for (auto v : ey[u]) {
        if (v == fy[u][0]) continue;
        fy[v][0] = u, dy[v] = dy[u] + 1;
        for (int i = 1; i < M; i++) fy[v][i] = fy[fy[v][i - 1]][i - 1];
        dfsy(v);
    }
}
int lca(int u, int v) {
    if (dy[u] < dy[v]) swap(u, v);
    for (int i = M - 1; ~i; i--)
        if (dy[fy[u][i]] >= dy[v]) u = fy[u][i];
    if (u == v) return u;
    for (int i = M - 1; ~i; i--)
        if (fy[u][i] != fy[v][i]) u = fy[u][i], v = fy[v][i];
    return fy[u][0];
}
