    while(l<r){
        int u=deq[l++];
        vis[u]=1;
        for(int i=Ehead[u];i;i=E[i].nex){
            int v=E[i].v;
            if(vis[v]) continue;
            dis[v]=dis[u]+1;
            if(dis[v]<dep[v])
                deq[r++]=v;
        }
    }
}

