struct graph {
    vector<int> g[MAXN];
    int fa[MAXN][19], dep[MAXN];

    void connect(int u, int v) {
        g[u].push_back(v);
        g[v].push_back(u);
    }

    void build(int u = s, int pre = 0) {
        fa[u][0] = pre;
        dep[u] = dep[pre] + 1;
        for (int i = 1; i <= 18; ++i)
            fa[u][i] = fa[fa[u][i - 1]][i - 1];
        for (int i = 0; i < (int)g[u].size(); ++i) {
            int v = g[u][i];
            if (v == pre)
                continue;
            build(v, u);
        }
    }

    int query(int u, int v) {
        if (dep[u] < dep[v])
            swap(u, v);
        for (int i = 18; i >= 0; --i)
            if (dep[fa[u][i]] >= dep[v])
                u = fa[u][i];
        if (u == v)
            return u;
        for (int i = 18; i >= 0; --i)
            if (fa[u][i] != fa[v][i]) {
                u = fa[u][i];
                v = fa[v][i];
            }
        return fa[u][0];
    }

    int calc(int u, int v) {
        return dep[u] + dep[v] - dep[query(u, v)] * 2;
    }
}A, B;


